function PSIpar=PsiInit(minThres,maxThres,thresStep,...		minSlope,maxSlope,slopeStep,minCont,maxCont,contStep,...		miss, gamma,pcorr)% PSIpar=PsiInit(minThres,maxThres,thresStep,...%		minSlope,maxSlope,slopeStep,minCont,maxCont,contStep,...%		miss, gamma,pcorr)% This script initialize parameter values for PSI procedure%%12/09/97	CCC, LLk, pull it out from main.m%3/02/98	CCC	Rewote to functional form for MATLAB 5.x +%Initialize the PSI tables% Init PSI% Set up the threshold range for estimationthresRange = [minThres:thresStep:maxThres+0.0001];% Set up the slope range for estimationslopeRange=exp(log(minSlope):log(2^slopeStep):log(maxSlope));% Possible contrast to be presented in an experimental sectioncontRange = [minCont:contStep:maxCont+0.0001];% make a probability table for updating likelihood pr1ax = []; %probability of correct response given a fixed threshold and slopem=length(thresRange);n=length(slopeRange);% slopes vary faster than thresholdsfor i=1:m  for j=1:n    pr1ax = [pr1ax; dprime(thresRange(i),slopeRange(j),contRange,pcorr)];  endendpr1ax = gamma + (1-miss)*(1-gamma)*pr1ax;pr0ax = 1 - pr1ax;pab=ones([m*n, 1])/(m*n);  % Prior likelihood for all thresholds and slopes % Pack in a structurePSIpar.pr1ax=pr1ax;PSIpar.pr0ax=pr0ax;PSIpar.pab=pab;PSIpar.thresRange=thresRange;PSIpar.slopeRange=slopeRange;PSIpar.contRange=contRange;